keypad = [[0,0,1,0,0],[0,2,3,4,0],[5,6,7,8,9],[0,'A','B','C',0],[0,0,'D',0,0]]


currentLocation = keypad[0][1]
#print(currentLocation)
combination = ''
row = 2
column = 0
for i in words:
    #print (row, column)
    for char in i:
    #    print("char:", char, "row", row, "column", column)
        if char == 'U':
            if row > 0 and keypad[row-1][column]!= 0:             
                row -=1
        elif char == 'D':
            if row < 4 and keypad[row+1][column]!= 0:
                row +=1
        elif char == 'L':
            if column > 0 and keypad[row][column-1]!= 0:
                column -=1
        elif char == 'R':
            if column < 4 and keypad[row][column+1]!= 0:
                column +=1
    
    codedigit = keypad[row][column]
    combination += str(codedigit)
print(combination)
